// src/components/CartMovement.js
import React, { useState, useEffect, useRef } from 'react';
import { Scatter } from 'react-chartjs-2';
import { Chart as ChartJS, PointElement, LinearScale, Tooltip, Legend } from 'chart.js';
import Slider from '@mui/material/Slider';

ChartJS.register(PointElement, LinearScale, Tooltip, Legend);

const CartMovement = ({ data }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [latestData, setLatestData] = useState({ datasets: [{ data: [] }] });
  const [isUserInteracting, setIsUserInteracting] = useState(false);
  const bgImageRef = useRef(null);
  const cartImageRef = useRef(null);
  const lastProcessedIndexRef = useRef(-1);

  useEffect(() => {
    console.log('Received data in CartMovement:', data);
    if (data.length > 0 && !isUserInteracting) {
      const nextIndex = Math.max(currentIndex, lastProcessedIndexRef.current + 1);
      if (nextIndex < data.length) {
        const intervalId = setInterval(() => {
          setCurrentIndex(prevIndex => {
            const newIndex = prevIndex + 1;
            if (newIndex < data.length) {
              //lastProcessedIndexRef.current = newIndex;
              return newIndex;
            } else {
              clearInterval(intervalId);
              return prevIndex;
            }
          });
        }, 1000); // Adjust this value to control the speed of progression

        return () => clearInterval(intervalId);
      }
    }
  }, [data, isUserInteracting, currentIndex]);

  useEffect(() => {
    if (data.length > 0 && currentIndex < data.length) {
      setLatestData({
        datasets: [{
          label: 'Cart Position',
          data: [{ x: data[currentIndex].x_axis, y: data[currentIndex].y_axis }],
          backgroundColor: 'blue',
        }]
      });
    }
  }, [data, currentIndex]);

  useEffect(() => {
    // Preload images
    const bgImg = new Image();
    bgImg.src = '/bg-image.png';
    bgImg.onload = () => {
      bgImageRef.current = bgImg;
    };

    const cartImg = new Image();
    cartImg.src = '/cart-image.png';
    cartImg.onload = () => {
      cartImageRef.current = cartImg;
    };
  }, []);

  const handleSliderChange = (event, newValue) => {
    setIsUserInteracting(true);
    setCurrentIndex(newValue);
  };

  const handleSliderChangeCommitted = () => {
    setIsUserInteracting(false);
    lastProcessedIndexRef.current = currentIndex - 1;
  };

  const backgroundPlugin = {
    id: 'background',
    beforeDraw: (chart) => {
      if (chart.chartArea && bgImageRef.current) {
        const { ctx, chartArea } = chart;
        ctx.drawImage(bgImageRef.current, chartArea.left, chartArea.top, chartArea.width, chartArea.height);
      }
    }
  };

  const customPointPlugin = {
    id: 'customPoint',
    afterDraw: (chart) => {
      const { ctx } = chart;
      chart.data.datasets.forEach((dataset) => {
        dataset.data.forEach((value, index) => {
          const meta = chart.getDatasetMeta(0);
          const point = meta.data[index];
          if (point && cartImageRef.current) {
            ctx.drawImage(cartImageRef.current, point.x - 15, point.y - 15, 30, 30);
          }
        });
      });
    }
  };

  return (
    <div>
      <h1>Cart Movement</h1>
      <Scatter
        data={latestData}
        options={{
          scales: {
            x: {
              type: 'linear',
              position: 'bottom',
              min: 0,
              max: 200,
            },
            y: {
              type: 'linear',
              position: 'left',
              min: 0,
              max: 250,
            },
          },
          elements: {
            point: {
              radius: 0
            }
          },
          animation: false
        }}
        plugins={[backgroundPlugin, customPointPlugin]}
      />
      <div>
        <Slider
          value={currentIndex}
          min={0}
          max={Math.max(0, data.length - 1)}
          onChange={handleSliderChange}
          onChangeCommitted={handleSliderChangeCommitted}
          aria-labelledby="cart-movement-slider"
          disabled={data.length === 0}
        />
      </div>
    </div>
  );
};

export default CartMovement;
