import React, { useState, useEffect } from 'react';
import { Scatter } from 'react-chartjs-2';
import { Chart as ChartJS, PointElement, LinearScale, Tooltip, Legend } from 'chart.js';

ChartJS.register(PointElement, LinearScale, Tooltip, Legend);

const CartMovement = ({ data }) => {
  const [chartData, setChartData] = useState({
    datasets: []
  });

  useEffect(() => {
    const datasets = Object.keys(data).map((tag) => ({
      label: `Tag: ${tag}`,
      data: data[tag].map(point => ({ x: point.x_axis, y: point.y_axis })),
      backgroundColor: 'blue',
    }));

    console.log('Datasets for the chart:', datasets);

    setChartData({ datasets });
  }, [data]);

  return (
    <div>
      <h1>Cart Movement</h1>
      <Scatter
        data={chartData}
        options={{
          scales: {
            x: {
              type: 'linear',
              position: 'bottom',
              min: 0,
              max: 200,
              ticks: { display: true },
              grid: { drawBorder: true, display: true },
            },
            y: {
              type: 'linear',
              position: 'left',
              min: 0,
              max: 250,
              ticks: { display: true, beginAtZero: true },
              grid: { drawBorder: true, display: true },
            },
          },
          elements: {
            point: { radius: 5, backgroundColor: 'blue' }
          },
          animation: false
        }}
      />
    </div>
  );
};

export default CartMovement;
