import React, { useState, useEffect } from 'react';
import mqtt from 'mqtt';
import CartMovement from './components/CartMovement';

const MQTT_BROKER = 'ws://broker.hivemq.com:8000/mqtt';
const MQTT_TOPIC = 'cart/position';

const App = () => {
  const [data, setData] = useState({});

  useEffect(() => {
    const client = mqtt.connect(MQTT_BROKER);

    client.on('connect', () => {
      console.log('Connected to MQTT broker');
      client.subscribe(MQTT_TOPIC, (err) => {
        if (err) {
          console.error('Subscription error:', err);
        }
      });
    });

    client.on('message', (topic, message) => {
      if (topic === MQTT_TOPIC) {
        const newData = JSON.parse(message.toString());
        const { tag_id } = newData;

        setData((prevData) => {
          const updatedTagData = prevData[tag_id] ? [...prevData[tag_id], newData] : [newData];
          const updatedData = { ...prevData, [tag_id]: updatedTagData };
          localStorage.setItem('cartData', JSON.stringify(updatedData));
          return updatedData;
        });
      }
    });

    return () => {
      if (client) {
        client.end();
      }
    };
  }, []);

  return (
    <div>
      <h1>Data Points</h1>
      {Object.keys(data).map((tag_id) => (
        <CartMovement key={tag_id} tag={tag_id} data={data[tag_id]} />
      ))}
    </div>
  );
};

export default App;
