import React, { useState, useEffect } from 'react';
import { Scatter } from 'react-chartjs-2';
import { Chart as ChartJS, PointElement, LinearScale, Tooltip, Legend } from 'chart.js';

ChartJS.register(PointElement, LinearScale, Tooltip, Legend);

const getColor = (() => {
  const colors = {};
  const randomColor = () => `hsl(${Math.random() * 360}, 100%, 50%)`;
  return tag => {
    if (!colors[tag]) {
      colors[tag] = randomColor();
    }
    return colors[tag];
  };
})();

const CartMovement = ({ data }) => {
  const [latestData, setLatestData] = useState({ datasets: [] });

  useEffect(() => {
    if (data.length > 0) {
      // Prepare datasets for each tag with the latest data
      const datasets = data.map(dataPoint => ({
        label: `Tag: ${dataPoint.tag}`,
        data: [{ x: dataPoint.x_axis, y: dataPoint.y_axis }],
        backgroundColor: getColor(dataPoint.tag),
      }));

      setLatestData({ datasets });
    }
  }, [data]);

  return (
    <div>
      <h1>Cart Movement</h1>
      <Scatter
        data={latestData}
        options={{
          scales: {
            x: {
              type: 'linear',
              position: 'bottom',
              min: 0,
              max: 200,
              ticks: {
                display: false,
              },
              grid: {
                drawBorder: false,
                display: false,
              },
            },
            y: {
              type: 'linear',
              position: 'left',
              min: 0,
              max: 250,
              ticks: {
                display: false,
                beginAtZero: true,
              },
              grid: {
                drawBorder: false,
                display: false,
              },
            },
          },
          elements: {
            point: {
              radius: 5,
            }
          },
          animation: false
        }}
      />
    </div>
  );
};

export default CartMovement;
