import React, { useState, useEffect, useRef, useCallback } from 'react';
import { Scatter } from 'react-chartjs-2';
import { Chart as ChartJS, PointElement, LinearScale, Tooltip, Legend } from 'chart.js';

ChartJS.register(PointElement, LinearScale, Tooltip, Legend);

const CartMovement = ({ data }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [latestData, setLatestData] = useState({ datasets: [{ data: [] }] });
  const bgImageRef = useRef(null);
  const cartImageRef = useRef(null);
  const playbackIntervalRef = useRef(null);

  useEffect(() => {
    if (data.length > 0) {
      const lastIndex = data.length - 1;
      if (currentIndex !== lastIndex) {
        setCurrentIndex(lastIndex);
      }
    }
  }, [data, currentIndex]);

  useEffect(() => {
    if (data.length > 0 && currentIndex < data.length) {
      setLatestData({
        datasets: [{
          label: 'Cart Position',
          data: [{ x: data[currentIndex].x_axis, y: data[currentIndex].y_axis }],
          backgroundColor: 'blue',
        }]
      });
    }
  }, [data, currentIndex]);

  useEffect(() => {
    const bgImg = new Image();
    bgImg.src = '/bg-image.png';
    bgImg.onload = () => {
      bgImageRef.current = bgImg;
    };

    const cartImg = new Image();
    cartImg.src = '/cart-image.png';
    cartImg.onload = () => {
      cartImageRef.current = cartImg;
    };
  }, []);

  const backgroundPlugin = {
    id: 'background',
    beforeDraw: (chart) => {
      if (chart.chartArea && bgImageRef.current) {
        const { ctx, chartArea } = chart;
        ctx.drawImage(bgImageRef.current, chartArea.left, chartArea.top, chartArea.width, chartArea.height);
      }
    }
  };

  const customPointPlugin = {
    id: 'customPoint',
    afterDraw: (chart) => {
      const { ctx } = chart;
      chart.data.datasets.forEach((dataset) => {
        dataset.data.forEach((value, index) => {
          const meta = chart.getDatasetMeta(0);
          const point = meta.data[index];
          if (point && cartImageRef.current) {
            ctx.drawImage(cartImageRef.current, point.x - 15, point.y - 15, 30, 30);
          }
        });
      });
    }
  };

  const handleSliderChange = useCallback((event) => {
    const index = parseInt(event.target.value);
    setCurrentIndex(index);
    if (playbackIntervalRef.current) {
      clearInterval(playbackIntervalRef.current);
      playbackIntervalRef.current = null;
    }
  }, []);

  const handlePlay = () => {
    if (playbackIntervalRef.current) return;
    playbackIntervalRef.current = setInterval(() => {
      setCurrentIndex((prevIndex) => {
        if (prevIndex < data.length - 1) {
          return prevIndex + 1;
        } else {
          clearInterval(playbackIntervalRef.current);
          playbackIntervalRef.current = null;
          return prevIndex;
        }
      });
    }, 1000); // Adjust the interval as needed
  };

  const handlePause = () => {
    if (playbackIntervalRef.current) {
      clearInterval(playbackIntervalRef.current);
      playbackIntervalRef.current = null;
    }
  };

  return (
    <div>
      <h1>Cart Movement</h1>
      <Scatter
        data={latestData}
        options={{
          scales: {
            x: {
              type: 'linear',
              position: 'bottom',
              min: 0,
              max: 200,
            },
            y: {
              type: 'linear',
              position: 'left',
              min: 0,
              max: 250,
            },
          },
          elements: {
            point: {
              radius: 0
            }
          },
          animation: false
        }}
        plugins={[backgroundPlugin, customPointPlugin]}
      />
      <input
        type="range"
        min="0"
        max={data.length - 1}
        value={currentIndex}
        onChange={handleSliderChange}
      />
      <div>
        <button onClick={handlePlay}>Play</button>
        <button onClick={handlePause}>Pause</button>
      </div>
    </div>
  );
};

export default CartMovement;
