import React, { useState, useEffect, useRef } from 'react';
import { Scatter } from 'react-chartjs-2';
import { Chart as ChartJS, PointElement, LinearScale, Tooltip, Legend } from 'chart.js';
import Slider from '@mui/material/Slider';

ChartJS.register(PointElement, LinearScale, Tooltip, Legend);

const CartMovement = ({ data }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [latestData, setLatestData] = useState({ datasets: [] });

  const bgImageRef = useRef(null);
  const cartImageRef = useRef(null);
  const autoPullTimeout = useRef(null);

  useEffect(() => {
    if (Object.keys(data).length > 0) {
      const datasets = Object.keys(data).map((tag, index) => ({
        label: `Cart ${tag}`,
        data: data[tag].map((d) => ({ x: d.x_axis, y: d.y_axis })),
        backgroundColor: index === 0 ? 'blue' : 'red', // Different color for each cart
      }));

      setLatestData({ datasets });
    }
  }, [data]);

  const handleSliderChange = (event, newValue) => {
    setCurrentIndex(newValue);

    const datasets = Object.keys(data).map((tag, index) => ({
      label: `Cart ${tag}`,
      data: [data[tag][newValue]],
      backgroundColor: index === 0 ? 'blue' : 'red', // Different color for each cart
    }));

    setLatestData({ datasets });

    // Clear any existing timeout
    if (autoPullTimeout.current) {
      clearTimeout(autoPullTimeout.current);
    }

    // Start auto-pull from the selected point to the latest point
    autoPullTimeout.current = setTimeout(() => {
      autoPullToLatest(newValue);
    }, 1000); // Adjust the delay as needed
  };

  const autoPullToLatest = (startIndex) => {
    const lastIndex = Math.max(...Object.keys(data).map(tag => data[tag].length - 1));

    const iterateToLatest = (index) => {
      if (index <= lastIndex) {
        setCurrentIndex(index);

        const datasets = Object.keys(data).map((tag, index) => ({
          label: `Cart ${tag}`,
          data: [data[tag][index]],
          backgroundColor: index === 0 ? 'blue' : 'red', // Different color for each cart
        }));

        setLatestData({ datasets });

        autoPullTimeout.current = setTimeout(() => {
          iterateToLatest(index + 1);
        }, 500); // Adjust the delay as needed for smoother animation
      }
    };

    iterateToLatest(startIndex);
  };

  useEffect(() => {
    // Preload images
    const bgImg = new Image();
    bgImg.src = '/bg-image.png';
    bgImg.onload = () => {
      bgImageRef.current = bgImg;
    };

    const cartImg = new Image();
    cartImg.src = '/cart-image.png';
    cartImg.onload = () => {
      cartImageRef.current = cartImg;
    };
  }, []);

  const backgroundPlugin = {
    id: 'background',
    beforeDraw: (chart) => {
      if (chart.chartArea && bgImageRef.current) {
        const { ctx, chartArea } = chart;
        ctx.drawImage(bgImageRef.current, chartArea.left, chartArea.top, chartArea.width, chartArea.height);
      }
    }
  };

  const customPointPlugin = {
    id: 'customPoint',
    afterDraw: (chart) => {
      const { ctx } = chart;
      chart.data.datasets.forEach((dataset) => {
        dataset.data.forEach((value, index) => {
          const meta = chart.getDatasetMeta(0);
          const point = meta.data[index];
          if (point && cartImageRef.current) {
            const color = dataset.backgroundColor;
            ctx.save();
            ctx.globalAlpha = 0.5;
            ctx.drawImage(cartImageRef.current, point.x - 15, point.y - 15, 30, 30);
            ctx.restore();
          }
        });
      });
    }
  };

  useEffect(() => {
    // Update the chart to show the latest coordinate on initial load
    if (Object.keys(data).length > 0) {
      const lastIndex = Math.max(...Object.keys(data).map(tag => data[tag].length - 1));
      setCurrentIndex(lastIndex);

      const datasets = Object.keys(data).map((tag, index) => ({
        label: `Cart ${tag}`,
        data: [data[tag][lastIndex]],
        backgroundColor: index === 0 ? 'blue' : 'red', // Different color for each cart
      }));

      setLatestData({ datasets });
    }
  }, [data]);

  const maxIndex = Math.max(...Object.keys(data).map(tag => data[tag].length - 1));

  return (
    <div>
      <h2>Cart Movements</h2>
      <Scatter
        data={latestData}
        options={{
          scales: {
            x: {
              type: 'linear',
              position: 'bottom',
              min: 0,
              max: 200,
              ticks: {
                display: false,
              },
              grid: {
                drawBorder: false,
                display: false,
              },
            },
            y: {
              type: 'linear',
              position: 'left',
              min: 0,
              max: 250,
              ticks: {
                display: false,
                beginAtZero: true,
              },
              grid: {
                drawBorder: false,
                display: false,
              },
            },
          },
          elements: {
            point: {
              radius: 0
            }
          },
          animation: false
        }}
        plugins={[backgroundPlugin, customPointPlugin]}
      />
      <div>
        <Slider
          value={currentIndex}
          min={0}
          max={maxIndex}
          onChange={handleSliderChange}
          aria-labelledby="cart-movement-slider"
        />
      </div>
    </div>
  );
};

export default CartMovement;
