import React, { useState, useEffect } from 'react';
import { Scatter } from 'react-chartjs-2';
import { Chart as ChartJS, PointElement, LinearScale, Tooltip, Legend } from 'chart.js';

ChartJS.register(PointElement, LinearScale, Tooltip, Legend);

// Predefined colors for tags
const colors = ['red', 'blue', 'green', 'orange', 'purple', 'cyan', 'magenta', 'yellow', 'brown', 'grey'];
const getColor = (() => {
  const colorMap = {};
  return tag => {
    if (!colorMap[tag]) {
      colorMap[tag] = colors[Object.keys(colorMap).length % colors.length];
    }
    return colorMap[tag];
  };
})();

const CartMovement = ({ data }) => {
  const [latestData, setLatestData] = useState({ datasets: [] });

  useEffect(() => {
    if (data.length > 0) {
      // Get the latest movement for each tag
      const latestMovements = data.reduce((acc, dataPoint) => {
        acc[dataPoint.tag] = dataPoint;
        return acc;
      }, {});

      const datasets = Object.keys(latestMovements).map(tag => ({
        label: `Tag: ${tag}`,
        data: [{ x: latestMovements[tag].x_axis, y: latestMovements[tag].y_axis }],
        backgroundColor: getColor(tag),
      }));

      console.log('Updated datasets:', datasets);
      setLatestData({ datasets });
    }
  }, [data]);

  return (
    <div>
      <h1>Cart Movement</h1>
      <Scatter
        data={latestData}
        options={{
          scales: {
            x: {
              type: 'linear',
              position: 'bottom',
              min: 0,
              max: 200,
              ticks: {
                display: false,
              },
              grid: {
                drawBorder: false,
                display: false,
              },
            },
            y: {
              type: 'linear',
              position: 'left',
              min: 0,
              max: 250,
              ticks: {
                display: false,
                beginAtZero: true,
              },
              grid: {
                drawBorder: false,
                display: false,
              },
            },
          },
          elements: {
            point: {
              radius: 5,
            }
          },
          animation: false
        }}
      />
    </div>
  );
};

export default CartMovement;
