import React, { useState, useEffect } from 'react';
import mqtt from 'mqtt';
import CartMovement from './components/CartMovement';

const MQTT_BROKER = 'ws://broker.hivemq.com:8000/mqtt';
const MQTT_TOPIC = 'cart/position';

const App = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    const client = mqtt.connect(MQTT_BROKER);

    client.on('connect', () => {
      console.log('Connected to MQTT broker');
      client.subscribe(MQTT_TOPIC, (err) => {
        if (err) {
          console.error('Subscription error:', err);
        }
      });
    });

    client.on('message', (topic, message) => {
      if (topic === MQTT_TOPIC) {
        const receivedData = JSON.parse(message.toString());

        setData((prevData) => {
          // Ensure receivedData is always an array
          const newDataArray = Array.isArray(receivedData) ? receivedData : [receivedData];

          // Create a map of existing data points by their unique identifier
          const existingIds = new Set(prevData.map((dataPoint) => dataPoint.id));

          // Filter out any new data points that already exist in the map
          const updatedData = [
            ...prevData,
            ...newDataArray.filter((dataPoint) => !existingIds.has(dataPoint.id)),
          ];

          // Store the updated data in localStorage
          localStorage.setItem('cartData', JSON.stringify(updatedData));
          return updatedData;
        });
      }
    });

    return () => {
      if (client) {
        client.end();
      }
    };
  }, []);

  return (
    <div>
      <h1>Data Points</h1>
      <CartMovement data={data} />
    </div>
  );
};

export default App;
